mapper.update:
update interview
set P_id = #{pId,jdbcType=INTEGER},
  I_date = #{iDate,jdbcType=DATE},
  I_time = #{iTime,jdbcType=CHAR},
  D_id = #{dId,jdbcType=INTEGER}
where I_id = #{iId,jdbcType=INTEGER}

mapper.select:
select I_id, P_id, I_date, I_time, D_id
from interview
where I_id = #{iId,jdbcType=INTEGER}
