mapper.update:
update surgery
set O_id = #{oId,jdbcType=INTEGER},
  SUR_site = #{surSite,jdbcType=VARCHAR},
  SUR_mana = #{surMana,jdbcType=VARCHAR}
where SUR_id = #{surId,jdbcType=INTEGER}

mapper.select:
select SUR_id, O_id, SUR_site, SUR_mana
from surgery
where SUR_id = #{surId,jdbcType=INTEGER}
